package com.transport.utils;import android.app.Activity;import android.app.AlertDialog;import android.content.Context;import androidx.annotation.NonNull;import androidx.fragment.app.DialogFragment;import androidx.fragment.app.FragmentManager;public class DialogUtil {    private static final String TAG_FRAGMENT = "Dialog";    private static DialogFragment mDialogFragment;    public static void showProgressDialog(Activity pContext, FragmentManager pFragmentManager) {        if (pContext == null || pContext.isFinishing()) {            return;        }        if (mDialogFragment != null && mDialogFragment.isVisible()) {            return;        }        DialogFragment mPrevious = mDialogFragment;        mDialogFragment = new ProgressDialogFragment();        mDialogFragment.setCancelable(false);        ((ProgressDialogFragment) mDialogFragment).setCanceledOnTouchOutside(false);        if (mPrevious != null) {            pFragmentManager.beginTransaction().remove(mPrevious).commitAllowingStateLoss();        }        pFragmentManager.beginTransaction().add(mDialogFragment, TAG_FRAGMENT).commitAllowingStateLoss();    }    public static void dismissProgressDialog() {        if (mDialogFragment != null) {            mDialogFragment.dismissAllowingStateLoss();            mDialogFragment = null;        }    }    public static void alertDialog(@NonNull Context context, String msg) {        new AlertDialog.Builder(context)                .setTitle(android.R.string.dialog_alert_title)                .setMessage(msg)                .setCancelable(false)                .setPositiveButton(android.R.string.ok, null)                .create()                .show();    }    public static void alertDialog(@NonNull Context context, String title, String msg) {        new AlertDialog.Builder(context)                .setTitle(title)                .setMessage(msg)                .setCancelable(false)                .setPositiveButton(android.R.string.ok, null)                .create()                .show();    }    public static void onFailureDialog(@NonNull Context context, String message) {        new AlertDialog.Builder(context)                .setTitle("Error")                .setMessage(message)                .setCancelable(false)                .setPositiveButton(android.R.string.ok, null)                .create()                .show();    }    public static void onErrorDialog(@NonNull Context context, String message) {        new AlertDialog.Builder(context)                .setTitle("Oops...")                .setMessage(message)                .setCancelable(false)                .setPositiveButton(android.R.string.ok, null)                .create()                .show();    }}